import React , { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import ExpenseItem from "./components/ExpenseItem";
import "./expenses.css"
import Card from './Card';
import NewExpense from './components/NewExpense';
import ExpensesFilter from './components/Expencefilter';


function App() {
  const [filteryear,setfilteryear]=useState();
  const [expenses,setExpense]=useState([
    {id:0.1, title:"Car Insurance",date:new Date(2021,2,28),price:294.67
  },
  {id:0.2,title:"Toilet Paper",date:new Date(2021,8,13),price:94.12
},
{id:0.3,title:"New Tv",date:new Date(2021,3,11),price:799.49
},
{id:0.4,title:"New Desk(Wooden)",date:new Date(2021,6,11),price:450
}

  ]);
  const addexpense=(ex)=>{
setExpense((prevExpense)=>{ return[ex, ...prevExpense] })
console.log(expenses);


  }
  const onChangeFilter=(e)=>{
    setfilteryear(e);
    console.log(e);
  }
  const filteryearexpence=expenses.filter(expense=>{return expense.date.getFullYear().toString()===filteryear})


  return (
 <div>

   <Card
   className='expenses'>
      <ExpensesFilter onChangeFilter={onChangeFilter} />
<NewExpense addexpense={addexpense}/>
{filteryearexpence.length==0 && <h1>not found</h1>}
 {filteryearexpence.length>0 && filteryearexpence.map((expense)=>(

 <ExpenseItem title={expense.title} amount={expense.price} date={expense.date} key={expense.id}/>))}
</Card></div>
  );
}

export default App;
